mapper.insert:
/* emailLog.insertEmailLog */
INSERT INTO email_log (
	VS_CODE
	, UID
	, TITLE
	, CONTENTS
	, REG_DTTM
) VALUES (
	#{vsCode}
	, #{uid}
	, #{title}
	, #{contents}
	, CURRENT_TIMESTAMP()
)

mapper.update:
/* emailLog.updateEmailLog */
UPDATE email_log
SET
	RESULTS = #{results}
  , UDT_DTTM = CURRENT_TIMESTAMP()
WHERE UID = #{uid}
